import{_ as e,c as i,o as l,as as a}from"./chunks/framework.IsWcLOmr.js";const f=JSON.parse('{"title":"Effekt-Engine Entwicklungsframework","description":"","frontmatter":{},"headers":[],"relativePath":"de/effects/Functions.md","filePath":"de/effects/Functions.md","lastUpdated":1756926236000}'),n={name:"de/effects/Functions.md"};function r(d,t,o,s,g,h){return l(),i("div",null,[...t[0]||(t[0]=[a('<h1 id="effekt-engine-entwicklungsframework" tabindex="-1">Effekt-Engine Entwicklungsframework <a class="header-anchor" href="#effekt-engine-entwicklungsframework" aria-label="Permalink to &quot;Effekt-Engine Entwicklungsframework&quot;">​</a></h1><p>Eine Liste aller verfügbaren Funktionen des Hyperion-Moduls.</p><h2 id="ubersicht-funktionen" tabindex="-1">Übersicht Funktionen <a class="header-anchor" href="#ubersicht-funktionen" aria-label="Permalink to &quot;Übersicht Funktionen&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Funktion</th><th style="text-align:left;">Retuns</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">hyperion.ledCount</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Get the current LED count from the LED layout</td></tr><tr><td style="text-align:left;">hyperion.latchTime</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Get the current active latchtime in ms.</td></tr><tr><td style="text-align:left;">hyperion.imageWidth()</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Get the current image width, calculate positions for elements at the <a href="https://doc.qt.io/qt-6/coordsys.html#rendering" target="_blank" rel="noreferrer">coordinate system</a></td></tr><tr><td style="text-align:left;">hyperion.imageHeight()</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Get the current image height,calculate positions for elements at the <a href="https://doc.qt.io/qt-6/coordsys.html#rendering" target="_blank" rel="noreferrer">coordinate system</a></td></tr><tr><td style="text-align:left;">hyperion.imageCRotate()</td><td style="text-align:left;">-</td><td style="text-align:left;">Rotates the coordinate system at the center (0,0) by the given angle. See <a href="#hyperion-imagecrotate">hyperion.imageCRotate()</a></td></tr><tr><td style="text-align:left;">hyperion.imageCOffset()</td><td style="text-align:left;">-</td><td style="text-align:left;">Add a offset to the coordinate system. See <a href="#hyperion-imagecoffset">hyperion.imageCOffset()</a></td></tr><tr><td style="text-align:left;">hyperion.imageCShear()</td><td style="text-align:left;">-</td><td style="text-align:left;">Shear the coordinate system. See <a href="#hyperion-imagecshear">hyperion.imageCShear()</a></td></tr><tr><td style="text-align:left;">hyperion.imageResetT()</td><td style="text-align:left;">-</td><td style="text-align:left;">Resets all coordination system modifications done with hyperion.imageCRotate(), hyperion.imageCOffset(), hyperion.imageCShear()</td></tr><tr><td style="text-align:left;">hyperion.imageMinSize()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imageminsize">hyperion.imageMinSize()</a></td></tr><tr><td style="text-align:left;">hyperion.abort()</td><td style="text-align:left;">Boolean</td><td style="text-align:left;">If true, hyperion requests an effect abort, used in a while loop to repeat effect calculations and writing</td></tr><tr><td style="text-align:left;">hyperion.imageConicalGradient()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imageconicalgradient">hyperion.imageConicalGradient()</a></td></tr><tr><td style="text-align:left;">hyperion.imageRadialGradient()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imageradialgradient">hyperion.imageRadialGradient()</a></td></tr><tr><td style="text-align:left;">hyperion.imageLinearGradient()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imagelineargradient">hyperion.imageLinearGradient()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawLine()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawline">hyperion.imageDrawLine()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawPoint()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawpoint">hyperion.imageDrawPoint()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawPolygon()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawpolygon">hyperion.imageDrawPolygon()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawPie()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawpie">hyperion.imageDrawPie()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawRect()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawrect">hyperion.imageDrawRect()</a></td></tr><tr><td style="text-align:left;">hyperion.imageSolidFill()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imagesolidfill">hyperion.imageSolidFill()</a></td></tr><tr><td style="text-align:left;">hyperion.imageShow()</td><td style="text-align:left;">-</td><td style="text-align:left;">Hyperion shows the image you created with other <code>hyperion.image*</code> functions before. This is always the last step after you created the image with other hyperion.image* function</td></tr><tr><td style="text-align:left;">hyperion.imageSetPixel()</td><td style="text-align:left;">-</td><td style="text-align:left;">See <a href="#hyperion-imagesetpixel">hyperion.imageSetPixel()</a></td></tr><tr><td style="text-align:left;">hyperion.imageGetPixel()</td><td style="text-align:left;">Tuple</td><td style="text-align:left;">A <a href="https://www.tutorialspoint.com/python/python_tuples.htm" target="_blank" rel="noreferrer">Python tuple</a> RGB values for the requested position. See <a href="#hyperion-imagegetpixel">hyperion.imageGetPixel()</a></td></tr><tr><td style="text-align:left;">hyperion.imageSave()</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Create a snapshot of the current effect image and returns an ID. To display the snapshot do <code>hyperion.imageShow(ID)</code>. Snapshots are the <em>current</em> state of the picture</td></tr><tr><td style="text-align:left;">hyperion.setColor()</td><td style="text-align:left;">-</td><td style="text-align:left;">Not recommended, read why! See <a href="#hyperion-setcolor">hyperion.setColor()</a></td></tr><tr><td style="text-align:left;">hyperion.setImage()</td><td style="text-align:left;">-</td><td style="text-align:left;">hyperion.setImage(width, height, RGB_bytearray)</td></tr></tbody></table><h3 id="hyperion-imageminsize" tabindex="-1">hyperion.imageMinSize() <a class="header-anchor" href="#hyperion-imageminsize" aria-label="Permalink to &quot;hyperion.imageMinSize()&quot;">​</a></h3><p>Da <code>hyperion.imageWidth()</code> und <code>hyperion.imageHeight()</code> mit dem LED-Layout skaliert, kann eine Mindestgröße definiert werden, um mehr Pixel zum Arbeiten zu erhalten. Bedenke, dass das Verhältnis zwischen Breite/Höhe immer von der Einstellung der Benutzer-LED abhängt, du kannst es nicht erzwingen.</p><div class="warning custom-block"><p class="custom-block-title">Achtung</p><p>Sollte aufgerufen werden, bevor du mit dem zeichnen beginnst!</p></div><p><code>hyperion.imageMinSize(pixelX,pixelY)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">pixelX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Minimum Pixels at the x-axis of the image to draw on with <code>hyperion.image*</code> functions</td></tr><tr><td style="text-align:left;">pixelY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Minimum Pixels at the y-axis of the image to draw on with <code>hyperion.image*</code> functions</td></tr></tbody></table><h3 id="hyperion-imagecrotate" tabindex="-1">hyperion.imageCRotate() <a class="header-anchor" href="#hyperion-imagecrotate" aria-label="Permalink to &quot;hyperion.imageCRotate()&quot;">​</a></h3><p>Dreht das Koordinatensystem im Mittepunkt, der auf der x-Achse 0 und auf der y-Achse 0 ist, um den angegebenen Winkel im Uhrzeigersinn. Hinweis: Wenn du den Mittelpunkt des Koordinatensystems verschieben willst, kannst du hyperion.imageCOffset() verwenden. <strong>Die Drehung wird bis zum Ende des Effekts beibehalten</strong>. <br><code>hyperion.imageCRotate(angle)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">angle</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Angle of the rotation between <code>0</code> and <code>360</code>, clockwise</td></tr></tbody></table><h3 id="hyperion-imagecoffset" tabindex="-1">hyperion.imageCOffset() <a class="header-anchor" href="#hyperion-imagecoffset" aria-label="Permalink to &quot;hyperion.imageCOffset()&quot;">​</a></h3><p>Versatz zum Koordinatensystem an der x-Achse und y-Achse hinzufügen.</p><div class="warning custom-block"><p class="custom-block-title">Achtung</p><p>Änderungen am Koordinatensystem führen zu ungewohntem Verhalten einiger kürzerer Versionen anderer hyperion.image* Zeichenfunktionen</p></div><p><code>hyperion.imageCOffset(offsetX, offsetY)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">offsetX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Offset which is added to the coordinate system at the x-axis. Positive value moves to the right, negative to the left</td></tr><tr><td style="text-align:left;">offsetY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Offset which is added to the coordinate system at the y-axis. Positive value moves to the right, negative to the left</td></tr></tbody></table><h3 id="hyperion-imagecshear" tabindex="-1">hyperion.imageCShear() <a class="header-anchor" href="#hyperion-imagecshear" aria-label="Permalink to &quot;hyperion.imageCShear()&quot;">​</a></h3><p>Schneidet das Koordinatensystem in der Vertikalen und Horizontalen. Mehr Informationen zum Schneiden findet man hier: <a href="https://en.wikipedia.org/wiki/Shear_mapping" target="_blank" rel="noreferrer">Shear Mapping</a></p><div class="warning custom-block"><p class="custom-block-title">Achtung</p><p>Änderungen am Koordinatensystem führen zu ungewohntem Verhalten einiger kürzerer Versionen anderer hyperion.image* Zeichenfunktionen</p></div><p><code>hyperion.imageCShear(sh, sv)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">sh</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Horizontal pixels to shear</td></tr><tr><td style="text-align:left;">sv</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Vertical pixels to shear.</td></tr></tbody></table><h3 id="hyperion-imageconicalgradient" tabindex="-1">hyperion.imageConicalGradient() <a class="header-anchor" href="#hyperion-imageconicalgradient" aria-label="Permalink to &quot;hyperion.imageConicalGradient()&quot;">​</a></h3><p>Zeichnet einen konischen Farbverlauf auf das Bild, alle Argumente sind erforderlich. Füge die Parameter in der Reihenfolge der Zeilen unten hinzu. Kurze Erläuterung zu conical gradient in den QT Docs: <a href="https://doc.qt.io/qt-6/qconicalgradient.html#details" target="_blank" rel="noreferrer">Conical Gradient</a> <br><code>hyperion.imageConicalGradient(startX, startY, width, height, centerX, centerY, angle, bytearray)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the start point at the x-axis of the rectangle that contains the gradient</td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the start point at the y-axis of the rectangle that contains the gradient</td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the width of the rectangle</td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the height of the rectangle</td></tr><tr><td style="text-align:left;">centerX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the center of the gradient at the x-axis. For the center of the picture use <code>hyperion.imageWidth()*0.5</code>, don&#39;t forget to surround it with int() or round()</td></tr><tr><td style="text-align:left;">centerY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the center of the gradient at the y-axis. For the center of the picture use <code>hyperion.imageHeight()*0.5</code>, don&#39;t forget to surround it with int() or round()</td></tr><tr><td style="text-align:left;">angle</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the angle from <code>0</code> to <code>360</code>. Used to rotate the gradient at the center point.</td></tr><tr><td style="text-align:left;">bytearray</td><td style="text-align:left;">ByteArray</td><td style="text-align:left;">bytearray of (position,red,green,blue,alpha,position,red,green,blue,alpha,...). Could be repeated as often you need it, all values have ranges from 0 to 255. The position is a point where the red green blue values are assigned. <br> <strong>Example:</strong> <code>bytearray([0,255,0,0,255,255,0,255,0,255])</code> - this is a gradient which starts at 0 with color 255,0,0 and alpha 255 and ends at position 255 with color 0,255,0 and alpha 255. The colors in between are interpolation, so this example is a color shift from red to green from 0° to 360°.</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Kürzere Versionen von hyperion.imageConicalGradient()</p><p><code>hyperion.imageConicalGradient(centerX, centerY, angle, bytearray)</code> -&gt; startX und startY sind 0 und die Breite/Höhe ist max. -&gt; Ganzes Bild</p></div><h3 id="hyperion-imageradialgradient" tabindex="-1">hyperion.imageRadialGradient() <a class="header-anchor" href="#hyperion-imageradialgradient" aria-label="Permalink to &quot;hyperion.imageRadialGradient()&quot;">​</a></h3><p>Zeichnet einen radialen Farbverlauf auf das Bild. Füge die Parameter in der unten angegebenen Reihenfolge hinzu. Alle Argumente sind erforderlich. Kurze Beschreibung bei QT Docs: <a href="https://doc.qt.io/qt-6/qradialgradient.html#details" target="_blank" rel="noreferrer">Radial Gradient</a> <br><code>hyperion.imageRadialGradient(startX, startY, width, height, centerX, centerY, radius, focalX, focalY, focalRadius, bytearray, spread)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis of the rectangle which contains the gradient.</td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis of the rectangle which contains the gradient.</td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">width of the rectangle.</td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">height of the rectangle.</td></tr><tr><td style="text-align:left;">centerX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the center at the x-axis of the gradient. For the center of the picture use <code>hyperion.imageWidth()*0.5</code>, don&#39;t forget to surround it with int() or round()</td></tr><tr><td style="text-align:left;">centerY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the center at the y-axis of the gradient. For the center of the picture use <code>hyperion.imageHeight()*0.5</code>, don&#39;t forget to surround it with int() or round()</td></tr><tr><td style="text-align:left;">radius</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the radius of the gradient in pixels</td></tr><tr><td style="text-align:left;">focalX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the focal point at the x-axis</td></tr><tr><td style="text-align:left;">focalY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the focal point at the y-axis</td></tr><tr><td style="text-align:left;">focalRadius</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the radius of the focal point</td></tr><tr><td style="text-align:left;">bytearray</td><td style="text-align:left;">ByteArray</td><td style="text-align:left;">bytearray of (position,red,green,blue,position,red,green,blue,...). Could be repeated as often you need it, all values have ranges from 0 to 255. The position is a point where the red green blue values are assigned <br> <strong>Example:</strong> <code>bytearray([0,255,0,0,255,0,255,0])</code> - this is a gradient which starts at 0 with color 255,0,0 and ends at position 255 with color 0,255,0. The colors in between are interpolation, so this example is a color shift from red to green.</td></tr><tr><td style="text-align:left;">spread</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the spread method outside the gradient. Available spread modes are: <br> <code>0</code> -&gt; The area is filled with the closest stop color <br> <code>1</code> -&gt; The gradient is reflected outside the gradient area <br> <code>2</code> -&gt; The gradient is repeated outside the gradient area <br> Please note that outside means <em>inside</em> the rectangle but outside of the gradient start and end points, so if these points are the same, you don&#39;t see the spread mode. A picture to the spread modes can you find here: <a href="https://doc.qt.io/qt-6/qradialgradient.html#details" target="_blank" rel="noreferrer">Spread modes</a></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Kürzere Versionen von hyperion.imageRadialGradient()</p><ul><li><code>hyperion.imageRadialGradient(startX, startY, width, height, centerX, centerY, radius, bytearray, spread)</code> -&gt; focalX, focalY, focalRadius erhalten ihre Werte aus centerX, centerY und radius</li><li><code>hyperion.imageRadialGradient(centerX, centerY, radius, focalX, focalY, focalRadius, bytearray, spread)</code> -&gt; startX und startY sind 0</li><li><code>hyperion.imageRadialGradient(centerX, centerY, radius, bytearray, spread)</code> -&gt; startX und startY sind 0 &amp; focalX, focalY, focalRadius erhalten ihre Werte aus centerX, centerY und radius</li></ul></div><h3 id="hyperion-imagelineargradient" tabindex="-1">hyperion.imageLinearGradient() <a class="header-anchor" href="#hyperion-imagelineargradient" aria-label="Permalink to &quot;hyperion.imageLinearGradient()&quot;">​</a></h3><p>Zeichnet einen linearen Farbverlauf auf das Bild. Füge die Parameter in der unten angegebenen Reihenfolge hinzu. Alle Argumente sind erforderlich. Kurze Beschreibung bei QT Docs: <a href="https://doc.qt.io/qt-6/qlineargradient.html#details" target="_blank" rel="noreferrer">Linear Gradient</a> <br><code>hyperion.imageLinearGradient(startRX, startRY, width, height, startX, startY, endX, endY, bytearray, spread)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startRX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis of the rectangle which contains the gradient.</td></tr><tr><td style="text-align:left;">startRY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis of the rectangle which contains the gradient.</td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">width of the rectangle.</td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">height of the rectangle.</td></tr><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the start at the x-axis for the gradient.</td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the start at the y-axis for the gradient.</td></tr><tr><td style="text-align:left;">endX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the end at the x-axis for the gradient.</td></tr><tr><td style="text-align:left;">endY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the end at the y-axis for the gradient.</td></tr><tr><td style="text-align:left;">bytearray</td><td style="text-align:left;">ByteArray</td><td style="text-align:left;">bytearray of (position,red,green,blue,alpha,position,red,green,blue,alpha,...). Could be repeated as often you need it, all values have ranges from 0 to 255. The position is a point where the red green blue values are assigned. <br> <strong>Example:</strong> <code>bytearray([0,255,0,0,255,255,0,255,0,127])</code> this is a gradient which starts at 0 with color 255,0,0 and alpha 255 and ends at position 255 with color 0,255,0 and alpha 127. The colors in between are interpolation, so this example is a color shift from red to green.</td></tr><tr><td style="text-align:left;">spread</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the spread method outside the gradient. Available spread modes are: <br> <code>0</code> -&gt; The area is filled with the closest stop color <br> <code>1</code> -&gt; The gradient is reflected outside the gradient area <br> <code>2</code> -&gt; The gradient is repeated outside the gradient area <br> Please note that outside means <em>inside</em> the rectangle but outside of the gradient start and end points, so if these points are the same, you don&#39;t see the spread mode. A picture to the spread modes can you find here: <a href="https://doc.qt.io/qt-6/qlineargradient.html#details" target="_blank" rel="noreferrer">Spread modes</a></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Kürzere Versionen von hyperion.imageLinearGradient()</p><p><code>hyperion.imageLinearGradient(startX, startY, endX, endY, bytearray, spread)</code> -&gt; Das Rechteck, das den Farbverlauf enthält, ist standardmäßig das gesamte Bild</p></div><h3 id="hyperion-imagedrawline" tabindex="-1">hyperion.imageDrawLine() <a class="header-anchor" href="#hyperion-imagedrawline" aria-label="Permalink to &quot;hyperion.imageDrawLine()&quot;">​</a></h3><p>Zeichnet eine Linie in das Bild. Alle Parameter sind erforderlich, außer a für Alpha. Füge die Parameter in der Reihenfolge der Zeilen unten hinzu. <br><code>hyperion.imageDrawLine(startX, startY, endX, endY, thick, r, g, b, a)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">endX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">end point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">endY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">end point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">thick</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Thickness of the line, should be calculated based on image height or width. But at least one Pixel. Example: <code>max(int(0.1*hyperion.imageHeight(),1)</code> is 10% of the image height.</td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Kürzere Versionen von hyperion.imageLinearGradient()</p><p><code>hyperion.imageLinearGradient(startX, startY, endX, endY, bytearray, spread)</code> -&gt; Das Rechteck, das den Farbverlauf enthält, ist standardmäßig das gesamte Bild</p></div><h3 id="hyperion-imagedrawpoint" tabindex="-1">hyperion.imageDrawPoint() <a class="header-anchor" href="#hyperion-imagedrawpoint" aria-label="Permalink to &quot;hyperion.imageDrawPoint()&quot;">​</a></h3><p>Zeichnet einen Punkt in das Bild. Alle Parameter sind erforderlich, außer a für Alpha. Füge die Parameter in der unten angegebenen Reihenfolge hinzu. <br><code>hyperion.imageDrawPoint(x, y, thick, r, g, b, a)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">x</td><td style="text-align:left;">Integer</td><td style="text-align:left;">point position at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">y</td><td style="text-align:left;">Integer</td><td style="text-align:left;">point position at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">thick</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Thickness of the point in pixel, should be calculated based on image height or width. But at least one Pixel. Example: <code>max(int(0.1*hyperion.imageHeight(),1)</code> is 10% of the image height.</td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Kürzere Versionen von hyperion.imageDrawPoint()</p><p><code>hyperion.imageDrawPoint(x, y, thick, r, g, b)</code> -&gt; Alpha ist standardmäßig 255</p></div><h3 id="hyperion-imagedrawpolygon" tabindex="-1">hyperion.imageDrawPolygon() <a class="header-anchor" href="#hyperion-imagedrawpolygon" aria-label="Permalink to &quot;hyperion.imageDrawPolygon()&quot;">​</a></h3><p>Zeichnet ein Polygon in das Bild und füllt es mit der bestimmten Farbe. Wird zum freien Formen verwendet (Dreieck, Sechseck,... was immer man will). Alle Parameter sind erforderlich, mit Ausnahme von a für Alpha. Füge die Parameter in der untenstehenden Reihenfolge hinzu. <br><code>hyperion.imageDrawPolygon(bytearray, r, g, b, a)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">bytearray</td><td style="text-align:left;">ByteArray</td><td style="text-align:left;">bytearray([point1X,point1Y,point2X,point2Y,point3X,point3Y,...]). Add pairs of X/Y coordinates to specific the corners of the polygon, each point has a X and a Y coordinate, you could add as much points as you need. The last point automatically connects to the first point.</td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Kürzere Versionen von hyperion.imageDrawPolygon()</p><p>hyperion.imageDrawPolygon(bytearray, r, g, b)` -&gt; Alpha ist standardmäßig 255</p></div><h3 id="hyperion-imagedrawpie" tabindex="-1">hyperion.imageDrawPie() <a class="header-anchor" href="#hyperion-imagedrawpie" aria-label="Permalink to &quot;hyperion.imageDrawPie()&quot;">​</a></h3><p>Zeichnet einen Kreis (auch bekannt als Kreisdiagramm) in das Bild und füllt ihn mit der bestimmten Farbe. Alle Parameter sind erforderlich, außer a für Alpha. Füge die Parameter in der Reihenfolge der Zeilen unten hinzu. <br><code>hyperion.imageDrawPie(centerX, centerY, radius, startAngle, spanAngle, r, g, b, a)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">centerX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">The center of the Pie at the x-axis</td></tr><tr><td style="text-align:left;">centerY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">The center of the Pie at the y-axis</td></tr><tr><td style="text-align:left;">radius</td><td style="text-align:left;">Integer</td><td style="text-align:left;">radius of the Pie in Pixels</td></tr><tr><td style="text-align:left;">startAngle</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start angle from <code>0</code> to <code>360</code>. <code>0</code> is at 3 o&#39;clock</td></tr><tr><td style="text-align:left;">spanAngle</td><td style="text-align:left;">Integer</td><td style="text-align:left;">span (wide) of the pie from <code>-360</code> to <code>360</code> which starts at the startAngle, positive values are counter-clockwise, negative clockwise</td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Kürzere Versionen von hyperion.imageDrawPie()</p><p><code>hyperion.imageDrawPie(centerX, centerY, radius, startAngle, spanAngle, r, g, b)</code> -&gt; alpha ist standardmäßig 255</p></div><h3 id="hyperion-imagedrawrect" tabindex="-1">hyperion.imageDrawRect() <a class="header-anchor" href="#hyperion-imagedrawrect" aria-label="Permalink to &quot;hyperion.imageDrawRect()&quot;">​</a></h3><p>Zeichnet ein Rechteck auf das Bild. Alle Parameter sind erforderlich, außer a für Alpha. Füge die Parameter in der Reihenfolge der Zeilen unten hinzu. <br><code>hyperion.imageDrawRect(startX, startY, width, height, thick, r, g, b, a,)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">width of the rectangle. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">height of the rectangle. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">thick</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Thickness of the rectangle, a good start value is <code>1</code></td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><h3 id="hyperion-imagesolidfill" tabindex="-1">hyperion.imageSolidFill() <a class="header-anchor" href="#hyperion-imagesolidfill" aria-label="Permalink to &quot;hyperion.imageSolidFill()&quot;">​</a></h3><p>Füllt einen bestimmten Teil des Bildes mit einer Volltonfarbe (oder ganz). Alle Parameter sind erforderlich. Füge die Parameter in der Reihenfolge der Zeilen unten hinzu. <br><code>hyperion.imageSolidFill(startX, startY, width, height, r, g, b, a)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">width of the fill area. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">height of the fill area. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;">alpha of the color from <code>0</code> to <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Kürzere Versionen von hyperion.imageSolidFill()</p><ul><li><code>hyperion.imageSolidFill(startX, startY, width, height, r, g, b)</code> -&gt; kein Alpha, Standardwert ist 255</li><li><code>hyperion.imageSolidFill(r, g, b, a)</code> -&gt; startX und startY ist 0, Breite und Höhe ist max. -&gt; Volles Bild</li><li><code>hyperion.imageSolidFill(r, g, b)</code> -&gt; startX und startY ist 0, Breite und Höhe ist max, Alpha 255. -&gt; Volles Bild</li></ul></div><h3 id="hyperion-imagesetpixel" tabindex="-1">hyperion.imageSetPixel() <a class="header-anchor" href="#hyperion-imagesetpixel" aria-label="Permalink to &quot;hyperion.imageSetPixel()&quot;">​</a></h3><p>Weist einer bestimmten Pixelposition eine Farbe zu. Alle Parameter sind erforderlich. Füge die Parameter in der Reihenfolge der Zeilen unten hinzu. <br><code>hyperion.imageSetPixel(X, Y, r, g, b)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">X</td><td style="text-align:left;">Integer</td><td style="text-align:left;">pixel point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">Y</td><td style="text-align:left;">Integer</td><td style="text-align:left;">pixel point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define blue color from <code>0</code> to <code>255</code></td></tr></tbody></table><h3 id="hyperion-imagegetpixel" tabindex="-1">hyperion.imageGetPixel() <a class="header-anchor" href="#hyperion-imagegetpixel" aria-label="Permalink to &quot;hyperion.imageGetPixel()&quot;">​</a></h3><p>Ermittelt eine Farbe an einer bestimmten Pixelposition. Alle Parameter sind erforderlich. Füge die Parameter in der Reihenfolge der Zeilen unten hinzu. <br><code>hyperion.imageGetPixel(X, Y)</code></p><table tabindex="0"><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">X</td><td style="text-align:left;">Integer</td><td style="text-align:left;">pixel point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">Y</td><td style="text-align:left;">Integer</td><td style="text-align:left;">pixel point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">Return</td><td style="text-align:left;">Tuple</td><td style="text-align:left;">Returns a Python Tuple of RGB values</td></tr></tbody></table><h3 id="hyperion-setcolor" tabindex="-1">hyperion.setColor() <a class="header-anchor" href="#hyperion-setcolor" aria-label="Permalink to &quot;hyperion.setColor()&quot;">​</a></h3><p>Setzt eine einzelne Farbe für alle LEDs mit <code>hyperion.setColor(255,0,0)</code>, so werden alle LEDs rot dargestellt. Es ist aber auch möglich, ein Bytearray von RGB-Werten zu senden. Jeder RGB-Wert in diesem Bytearray repräsentiert eine LED.</p><ul><li><strong>Beispiel 1:</strong> <code>hyperion.setColor(bytearray([255,0,0]))</code> Die erste LED wird rot</li><li><strong>Beispiel 2:</strong> <code>hyperion.setColor(bytearray([255,0,0,0,255,0]))</code> Die erste LED wird rot, die zweite ist grün</li><li><strong>Beispiel 3:</strong> <code>hyperion.setColor(bytearray([255,0,0,0,255,0,255,255,255]))</code> Die erste LED wird rot, die zweite ist grün und die dritte ist weiss</li><li>Normalerweise ordnet man allen LEDs eine Farbe zu, deshalb muss man wissen, wie viele LEDs der Benutzer gerade hat. Ermittle dies mit <code>hyperion.ledCount</code></li></ul><div class="warning custom-block"><p class="custom-block-title">Achtung</p><ul><li>Die Funktion hyperion.setColor() wird nicht empfohlen, um LED-Farben zuzuweisen, sie arbeitet nicht mit den <strong><code>hyperion.image*</code></strong>-Funktionen zusammen</li><li>Du weißt nicht, wo oben/links/rechts/unten ist und es funktioniert nicht mit Matrix-Layouts!</li><li>Bitte beachte, dass du stattdessen die <strong><code>hyperion.image*</code></strong>-Funktionen verwenden kannst, um beeindruckende Effekte zu erzeugen, die mit der Benutzereinstellung skalieren</li></ul></div>',67)])])}const c=e(n,[["render",r]]);export{f as __pageData,c as default};
