import{_ as a,c as i,o as s,as as n}from"./chunks/framework.CquQrE-Y.js";const c=JSON.parse('{"title":"Erste Schritte","description":"","frontmatter":{"outline":[2,3]},"headers":[],"relativePath":"de/user/GettingStarted.md","filePath":"de/user/GettingStarted.md","lastUpdated":1751475924000}'),t={name:"de/user/GettingStarted.md"};function r(l,e,o,d,h,p){return s(),i("div",null,e[0]||(e[0]=[n('<h1 id="erste-schritte" tabindex="-1">Erste Schritte <a class="header-anchor" href="#erste-schritte" aria-label="Permalink to &quot;Erste Schritte&quot;">​</a></h1><p>Hyperion kann über Installationspakete oder Zip-Archive auf zahlreichen Plattformen installiert werden. Die Konfiguration und Steuerung von Hyperion erfolgt über den Browser.</p><h2 id="voraussetzungen" tabindex="-1">Voraussetzungen <a class="header-anchor" href="#voraussetzungen" aria-label="Permalink to &quot;Voraussetzungen&quot;">​</a></h2><table><thead><tr><th>Unterstützte Systeme <span style="color:var(--vp-custom-block-warning-text);">*</span></th><th>Unterstützte Browser</th></tr></thead><tbody><tr><td>Raspberry Pi (siehe auch <a href="/de/user/HyperBian.html">HyperBian</a>)<br>Debian 10, Ubuntu 22.04, Fedora 42 oder neuer<br>Mac OS<br>Windows 10</td><td>Chrome 47+<br>Firefox 43+<br>Opera 34+<br>Safari 9.1+<br>Microsoft Edge 14+</td></tr><tr><td colspan="2" style="background-color:var(--vp-c-bg);font-size:8pt;color:var(--vp-custom-block-warning-text);">*Bitte beachte das einige arm-Geräte nur eine eingeschränkte Unterstützung für die Bildschirmaufnahme bieten</td></tr></tbody></table><h2 id="installieren" tabindex="-1">Installieren <a class="header-anchor" href="#installieren" aria-label="Permalink to &quot;Installieren&quot;">​</a></h2><h3 id="ubuntu-debian-und-konsorten-fedora-libreelec-macos-windows" tabindex="-1">Ubuntu, Debian und Konsorten, Fedora, LibreELEC, macOS, Windows <a class="header-anchor" href="#ubuntu-debian-und-konsorten-fedora-libreelec-macos-windows" aria-label="Permalink to &quot;Ubuntu, Debian und Konsorten, Fedora, LibreELEC, macOS, Windows&quot;">​</a></h3><p>Die Hyperion-Projekt <a href="https://releases.hyperion-project.org/" target="_blank" rel="noreferrer">Paket Repository Seite</a> bietet Installationsanweisungen auf Knopfdruck.</p><div class="tip custom-block"><p class="custom-block-title">Ein fertiges Installations-Image ist für Raspberry Pi Benutzer verfügbar</p><p>Installiere <a href="/de/user/HyperBian.html">HyperBian</a>, um ein komplettes System zu erhalten, das sofort einsatzbereit ist.</p></div><details class="details custom-block"><summary>Andere Distributionen basierend auf Ubuntu oder Debian</summary><p>Das gleiche einfache Installationsskript kann verwendet werden, aber der Codename der zugrunde liegenden Distribution muss durch eine zusätzliche Option angegeben werden</p><p><code>--ubuntu</code> <em>codebase name</em> oder <code>--debian</code> <em>codebase name</em></p><p>Beispiel für Pop!_OS 22.04 LTS oder Mint 21.2 Victoria (die auf Ubuntu &#39;jammy&#39; basieren)</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">curl</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -sSL</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://releases.hyperion-project.org/install</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> |</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> bash</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -s</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --ubuntu</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;jammy&#39;</span></span></code></pre></div></details><h2 id="aktualisieren" tabindex="-1">Aktualisieren <a class="header-anchor" href="#aktualisieren" aria-label="Permalink to &quot;Aktualisieren&quot;">​</a></h2><p>Falls Hyperion von der Paket-Repository-Seite oder einem deb/rpm-Standalone-Paket installiert wurde, folge dem Standardverfahren des Betriebssystems, um das System zu aktualisieren.</p><h3 id="ubuntu-debian-und-konsorten-hyperbian" tabindex="-1">Ubuntu, Debian und Konsorten + HyperBian <a class="header-anchor" href="#ubuntu-debian-und-konsorten-hyperbian" aria-label="Permalink to &quot;Ubuntu, Debian und Konsorten + HyperBian&quot;">​</a></h3><p>Öffnen ein Terminal oder verbinden Dich per ssh mit einem entfernten System und führe folgenden Befehl aus</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt-get</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> hyperion</span></span></code></pre></div><h3 id="fedora" tabindex="-1">Fedora <a class="header-anchor" href="#fedora" aria-label="Permalink to &quot;Fedora&quot;">​</a></h3><p>Öffne ein Terminal und führe folgenden Befehl aus</p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> dnf</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -y</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> upgrade</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> hyperion</span></span></code></pre></div><h3 id="libreelec" tabindex="-1">LibreELEC <a class="header-anchor" href="#libreelec" aria-label="Permalink to &quot;LibreELEC&quot;">​</a></h3><p>Siehe <a href="https://releases.hyperion-project.org/#easy" target="_blank" rel="noreferrer">hier</a>.</p><h2 id="deinstallieren" tabindex="-1">Deinstallieren <a class="header-anchor" href="#deinstallieren" aria-label="Permalink to &quot;Deinstallieren&quot;">​</a></h2><h3 id="ubuntu-debian-und-konsorten-libreelec-fedora-macos-windows" tabindex="-1">Ubuntu, Debian und Konsorten, LibreELEC, Fedora, macOS, Windows <a class="header-anchor" href="#ubuntu-debian-und-konsorten-libreelec-fedora-macos-windows" aria-label="Permalink to &quot;Ubuntu, Debian und Konsorten, LibreELEC, Fedora, macOS, Windows&quot;">​</a></h3><p>Die Hyperion-Projekt [Paket Repository Seite] (<a href="https://releases.hyperion-project.org/" target="_blank" rel="noreferrer">https://releases.hyperion-project.org/</a>) bietet entsprechende Anweisungen zur Deinstallation an.</p><h2 id="hyperion-benutzerdaten" tabindex="-1">Hyperion Benutzerdaten <a class="header-anchor" href="#hyperion-benutzerdaten" aria-label="Permalink to &quot;Hyperion Benutzerdaten&quot;">​</a></h2><p>Hyperion speichert Benutzerkonfigurationsdaten und angepasste Effekte im Home-Verzeichnis des ausführenden Benutzers (Ordner <code>.hyperion</code>).</p><p>Alternativ kanst Du den Speicherort auch auf der Seite &quot;Über Hyperion&quot; nachsehen. Der Eintrag <code>Config path:</code> gibt den Ort an, an dem die Benutzerdaten derzeit gespeichert sind.</p><h2 id="hyperion-linux-dienst" tabindex="-1">Hyperion Linux Dienst <a class="header-anchor" href="#hyperion-linux-dienst" aria-label="Permalink to &quot;Hyperion Linux Dienst&quot;">​</a></h2><p>In nicht-interaktiven (head-less) Umgebungen wird ein Hyperion Service installiert, der unter dem aktuellen Benutzer läuft. In den gleichen Fällen, z.B. bei Verwendung von ws281x LEDs, muss Hyperion unter `root laufen. Hyperion stellt ein Kommandozeilenprogramm zur Verfügung, mit dem der Benutzer, unter dem der Dienst läuft, gewechselt werden kann.</p><h3 id="andern-des-benutzers" tabindex="-1">Ändern des Benutzers <a class="header-anchor" href="#andern-des-benutzers" aria-label="Permalink to &quot;Ändern des Benutzers&quot;">​</a></h3><p><strong>...auf &#39;root&#39;</strong></p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> updateHyperionUser</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -u</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> root</span></span></code></pre></div><p><strong>...auf den aktuellen Benutzer</strong></p><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> updateHyperionUser</span></span></code></pre></div><h3 id="dienst-erstellen" tabindex="-1">Dienst erstellen <a class="header-anchor" href="#dienst-erstellen" aria-label="Permalink to &quot;Dienst erstellen&quot;">​</a></h3><p>Standardmäßig wird in interaktiven Umgebungen mit grafischer Benutzeroberfläche kein Hyperion-Dienst installiert. Wenn Hyperion als Systemdienst ausgeführt werden soll, kann der Dienst manuell erstellt werden.</p><ol><li>Kopiere die Hyperion Servicedatei</li></ol><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> cp</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /usr/share/hyperion/service/hyperion.systemd</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /etc/systemd/system/hyperion@.service</span></span></code></pre></div><ol start="2"><li>Richten den Dienst für den aktuellen Benutzer ein.<br> Wenn es ein anderer Benutzer sein soll, verwende die Option -u.</li></ol><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /usr/share/hyperion/scripts/updateHyperionUser.sh</span></span></code></pre></div><ol start="3"><li>Überprüfe, ob der Dienst für den aktuellen Benutzer läuft.<br> Ersetze ${USER} durch den Benutzernamen, wenn der Benutzer zuvor mit der Option -u festgelegt wurde.</li></ol><div class="language-sh vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">systemctl</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> status</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> hyperion@</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">${USER}</span></span></code></pre></div>',40)]))}const k=a(t,[["render",r]]);export{c as __pageData,k as default};
