import{_ as t,c as e,o as a,a5 as l}from"./chunks/framework.Cba3l4cj.js";const p=JSON.parse('{"title":"Effect Engine API","description":"","frontmatter":{},"headers":[],"relativePath":"effects/API.md","filePath":"effects/API.md","lastUpdated":1712319116000}'),i={name:"effects/API.md"},r=l('<h1 id="effect-engine-api" tabindex="-1">Effect Engine API <a class="header-anchor" href="#effect-engine-api" aria-label="Permalink to &quot;Effect Engine API&quot;">​</a></h1><p>All available functions for usage.</p><h2 id="api-overview" tabindex="-1">API Overview <a class="header-anchor" href="#api-overview" aria-label="Permalink to &quot;API Overview&quot;">​</a></h2><table><thead><tr><th style="text-align:left;">Function</th><th>Returns</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">hyperion.ledCount</td><td>Integer</td><td style="text-align:left;">Get the current LED count from the LED layout</td></tr><tr><td style="text-align:left;">hyperion.latchTime</td><td>Integer</td><td style="text-align:left;">Get the current active latchtime in ms.</td></tr><tr><td style="text-align:left;">hyperion.imageWidth()</td><td>Integer</td><td style="text-align:left;">Get the current image width, calculate positions for elements at the <a href="https://doc.qt.io/qt-5/coordsys.html#rendering" target="_blank" rel="noreferrer">coordinate system</a></td></tr><tr><td style="text-align:left;">hyperion.imageHeight()</td><td>Integer</td><td style="text-align:left;">Get the current image height,calculate positions for elements at the <a href="https://doc.qt.io/qt-5/coordsys.html#rendering" target="_blank" rel="noreferrer">coordinate system</a></td></tr><tr><td style="text-align:left;">hyperion.imageCRotate()</td><td>-</td><td style="text-align:left;">Rotates the coordinate system at the center (0,0) by the given angle. See <a href="#hyperion-imagecrotate">hyperion.imageCRotate()</a></td></tr><tr><td style="text-align:left;">hyperion.imageCOffset()</td><td>-</td><td style="text-align:left;">Add a offset to the coordinate system. See <a href="#hyperion-imagecoffset">hyperion.imageCOffset()</a></td></tr><tr><td style="text-align:left;">hyperion.imageCShear()</td><td>-</td><td style="text-align:left;">Shear the coordinate system. See <a href="#hyperion-imagecshear">hyperion.imageCShear()</a></td></tr><tr><td style="text-align:left;">hyperion.imageResetT()</td><td>-</td><td style="text-align:left;">Resets all coordination system modifications done with hyperion.imageCRotate(), hyperion.imageCOffset(), hyperion.imageCShear()</td></tr><tr><td style="text-align:left;">hyperion.imageMinSize()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imageminsize">hyperion.imageMinSize()</a></td></tr><tr><td style="text-align:left;">hyperion.abort()</td><td>Boolean</td><td style="text-align:left;">If true, hyperion requests an effect abort, used in a while loop to repeat effect calculations and writing</td></tr><tr><td style="text-align:left;">hyperion.imageConicalGradient()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imageconicalgradient">hyperion.imageConicalGradient()</a></td></tr><tr><td style="text-align:left;">hyperion.imageRadialGradient()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imageradialgradient">hyperion.imageRadialGradient()</a></td></tr><tr><td style="text-align:left;">hyperion.imageLinearGradient()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imagelineargradient">hyperion.imageLinearGradient()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawLine()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawline">hyperion.imageDrawLine()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawPoint()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawpoint">hyperion.imageDrawPoint()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawPolygon()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawpolygon">hyperion.imageDrawPolygon()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawPie()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawpie">hyperion.imageDrawPie()</a></td></tr><tr><td style="text-align:left;">hyperion.imageDrawRect()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imagedrawrect">hyperion.imageDrawRect()</a></td></tr><tr><td style="text-align:left;">hyperion.imageSolidFill()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imagesolidfill">hyperion.imageSolidFill()</a></td></tr><tr><td style="text-align:left;">hyperion.imageShow()</td><td>-</td><td style="text-align:left;">Hyperion shows the image you created with other <code>hyperion.image*</code> functions before. This is always the last step after you created the image with other hyperion.image* function</td></tr><tr><td style="text-align:left;">hyperion.imageSetPixel()</td><td>-</td><td style="text-align:left;">See <a href="#hyperion-imagesetpixel">hyperion.imageSetPixel()</a></td></tr><tr><td style="text-align:left;">hyperion.imageGetPixel()</td><td>Tuple</td><td style="text-align:left;">A <a href="https://www.tutorialspoint.com/python/python_tuples.htm" target="_blank" rel="noreferrer">Python tuple</a> RGB values for the requested position. See <a href="#hyperion-imagegetpixel">hyperion.imageGetPixel()</a></td></tr><tr><td style="text-align:left;">hyperion.imageSave()</td><td>Integer</td><td style="text-align:left;">Create a snapshot of the current effect image and returns an ID. To display the snapshot do <code>hyperion.imageShow(ID)</code>. Snapshots are the <em>current</em> state of the picture</td></tr><tr><td style="text-align:left;">hyperion.setColor()</td><td>-</td><td style="text-align:left;">Not recommended, read why! See <a href="#hyperion-setcolor">hyperion.setColor()</a></td></tr><tr><td style="text-align:left;">hyperion.setImage()</td><td>-</td><td style="text-align:left;">hyperion.setImage(width, height, RGB_bytearray)</td></tr></tbody></table><h3 id="hyperion-imageminsize" tabindex="-1">hyperion.imageMinSize() <a class="header-anchor" href="#hyperion-imageminsize" aria-label="Permalink to &quot;hyperion.imageMinSize()&quot;">​</a></h3><p>As the <code>hyperion.imageWidth()</code> and <code>hyperion.imageHeight()</code> scales with the LED layout, you could define a minimum size to get more pixels to work with. Keep in mind that the ratio between width/height depends always on user LED setup, you can&#39;t force it.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Should be called before you start painting!</p></div><p><code>hyperion.imageMinSize(pixelX,pixelY)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">pixelX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Minimum Pixels at the x-axis of the image to draw on with <code>hyperion.image*</code> functions</td></tr><tr><td style="text-align:left;">pixelY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Minimum Pixels at the y-axis of the image to draw on with <code>hyperion.image*</code> functions</td></tr></tbody></table><h3 id="hyperion-imagecrotate" tabindex="-1">hyperion.imageCRotate() <a class="header-anchor" href="#hyperion-imagecrotate" aria-label="Permalink to &quot;hyperion.imageCRotate()&quot;">​</a></h3><p>Rotates the coordinate system at the center which is 0 at the x-axis and 0 at the y-axis by the given angle clockwise. Note: If you want to move the center of the coordinate system you could use hyperion.imageCOffset(). <strong>The rotation is kept until the effect ends</strong>. <br><code>hyperion.imageCRotate(angle)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">angle</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Angle of the rotation between <code>0</code> and <code>360</code>, clockwise</td></tr></tbody></table><h3 id="hyperion-imagecoffset" tabindex="-1">hyperion.imageCOffset() <a class="header-anchor" href="#hyperion-imagecoffset" aria-label="Permalink to &quot;hyperion.imageCOffset()&quot;">​</a></h3><p>Add offset to the coordinate system at the x-axis and y-axis.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Changes at the coordinate system results in weird behavior of some shorter versions of other hyperion.image* drawing functions</p></div><p><code>hyperion.imageCOffset(offsetX, offsetY)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">offsetX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Offset which is added to the coordinate system at the x-axis. Positive value moves to the right, negative to the left</td></tr><tr><td style="text-align:left;">offsetY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Offset which is added to the coordinate system at the y-axis. Positive value moves to the right, negative to the left</td></tr></tbody></table><h3 id="hyperion-imagecshear" tabindex="-1">hyperion.imageCShear() <a class="header-anchor" href="#hyperion-imagecshear" aria-label="Permalink to &quot;hyperion.imageCShear()&quot;">​</a></h3><p>Shears the coordinate system at the vertical and horizontal. More info to shearing here: <a href="https://en.wikipedia.org/wiki/Shear_mapping" target="_blank" rel="noreferrer">Shear Mapping</a></p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Changes at the coordinate system results in weird behavior of some shorter versions of other hyperion.image* drawing functions</p></div><p><code>hyperion.imageCShear(sh, sv)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">sh</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Horizontal pixels to shear</td></tr><tr><td style="text-align:left;">sv</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Vertical pixels to shear.</td></tr></tbody></table><h3 id="hyperion-imageconicalgradient" tabindex="-1">hyperion.imageConicalGradient() <a class="header-anchor" href="#hyperion-imageconicalgradient" aria-label="Permalink to &quot;hyperion.imageConicalGradient()&quot;">​</a></h3><p>Draws a conical gradient on the image, all arguments are required. Add the arguments in the order of rows below. Short explanation for conical gradient at the QT docs: <a href="https://doc.qt.io/qt-5/qconicalgradient.html#details" target="_blank" rel="noreferrer">Conical Gradient</a> <br><code>hyperion.imageConicalGradient(startX, startY, width, height, centerX, centerY, angle, bytearray)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the start point at the x-axis of the rectangle that contains the gradient</td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the start point at the y-axis of the rectangle that contains the gradient</td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the width of the rectangle</td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the height of the rectangle</td></tr><tr><td style="text-align:left;">centerX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the center of the gradient at the x-axis. For the center of the picture use <code>hyperion.imageWidth()*0.5</code>, don&#39;t forget to surround it with int() or round()</td></tr><tr><td style="text-align:left;">centerY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the center of the gradient at the y-axis. For the center of the picture use <code>hyperion.imageHeight()*0.5</code>, don&#39;t forget to surround it with int() or round()</td></tr><tr><td style="text-align:left;">angle</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the angle from <code>0</code> to <code>360</code>. Used to rotate the gradient at the center point.</td></tr><tr><td style="text-align:left;">bytearray</td><td style="text-align:left;">ByteArray</td><td style="text-align:left;">bytearray of (position,red,green,blue,alpha,position,red,green,blue,alpha,...). Could be repeated as often you need it, all values have ranges from 0 to 255. The position is a point where the red green blue values are assigned. <br> <strong>Example:</strong> <code>bytearray([0,255,0,0,255,255,0,255,0,255])</code> - this is a gradient which starts at 0 with color 255,0,0 and alpha 255 and ends at position 255 with color 0,255,0 and alpha 255. The colors in between are interpolation, so this example is a color shift from red to green from 0° to 360°.</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Shorter versions of hyperion.imageConicalGradient()</p><p><code>hyperion.imageConicalGradient(centerX, centerY, angle, bytearray)</code> -&gt; startX and startY are 0 and the width/height is max. -&gt; Entire image</p></div><h3 id="hyperion-imageradialgradient" tabindex="-1">hyperion.imageRadialGradient() <a class="header-anchor" href="#hyperion-imageradialgradient" aria-label="Permalink to &quot;hyperion.imageRadialGradient()&quot;">​</a></h3><p>Draws a radial gradient on the image. Add the arguments in the order of rows below. All arguments are required. Short description at QT Docs: <a href="https://doc.qt.io/qt-5/qradialgradient.html#details" target="_blank" rel="noreferrer">Radial Gradient</a> <br><code>hyperion.imageRadialGradient(startX, startY, width, height, centerX, centerY, radius, focalX, focalY, focalRadius, bytearray, spread)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis of the rectangle which contains the gradient.</td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis of the rectangle which contains the gradient.</td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">width of the rectangle.</td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">height of the rectangle.</td></tr><tr><td style="text-align:left;">centerX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the center at the x-axis of the gradient. For the center of the picture use <code>hyperion.imageWidth()*0.5</code>, don&#39;t forget to surround it with int() or round()</td></tr><tr><td style="text-align:left;">centerY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the center at the y-axis of the gradient. For the center of the picture use <code>hyperion.imageHeight()*0.5</code>, don&#39;t forget to surround it with int() or round()</td></tr><tr><td style="text-align:left;">radius</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the radius of the gradient in pixels</td></tr><tr><td style="text-align:left;">focalX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the focal point at the x-axis</td></tr><tr><td style="text-align:left;">focalY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the focal point at the y-axis</td></tr><tr><td style="text-align:left;">focalRadius</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the radius of the focal point</td></tr><tr><td style="text-align:left;">bytearray</td><td style="text-align:left;">ByteArray</td><td style="text-align:left;">bytearray of (position,red,green,blue,position,red,green,blue,...). Could be repeated as often you need it, all values have ranges from 0 to 255. The position is a point where the red green blue values are assigned <br> <strong>Example:</strong> <code>bytearray([0,255,0,0,255,0,255,0])</code> - this is a gradient which starts at 0 with color 255,0,0 and ends at position 255 with color 0,255,0. The colors in between are interpolation, so this example is a color shift from red to green.</td></tr><tr><td style="text-align:left;">spread</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the spread method outside the gradient. Available spread modes are: <br> <code>0</code> -&gt; The area is filled with the closest stop color <br> <code>1</code> -&gt; The gradient is reflected outside the gradient area <br> <code>2</code> -&gt; The gradient is repeated outside the gradient area <br> Please note that outside means <em>inside</em> the rectangle but outside of the gradient start and end points, so if these points are the same, you don&#39;t see the spread mode. A picture to the spread modes can you find here: <a href="https://doc.qt.io/qt-5/qradialgradient.html#details" target="_blank" rel="noreferrer">Spread modes</a></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Shorter versions of hyperion.imageRadialGradient()</p><ul><li><code>hyperion.imageRadialGradient(startX, startY, width, height, centerX, centerY, radius, bytearray, spread)</code> -&gt; focalX, focalY, focalRadius get their values from centerX, centerY and radius</li><li><code>hyperion.imageRadialGradient(centerX, centerY, radius, focalX, focalY, focalRadius, bytearray, spread)</code> -&gt; startX and startY are 0</li><li><code>hyperion.imageRadialGradient(centerX, centerY, radius, bytearray, spread)</code> -&gt; startX and startY are 0 &amp; focalX, focalY, focalRadius get their values from centerX, centerY and radius</li></ul></div><h3 id="hyperion-imagelineargradient" tabindex="-1">hyperion.imageLinearGradient() <a class="header-anchor" href="#hyperion-imagelineargradient" aria-label="Permalink to &quot;hyperion.imageLinearGradient()&quot;">​</a></h3><p>Draws a linear gradient on the image. Add the arguments in the order of rows below. All arguments are required. Short description at QT Docs: <a href="https://doc.qt.io/qt-5/qlineargradient.html#details" target="_blank" rel="noreferrer">Linear Gradient</a> <br><code>hyperion.imageLinearGradient(startRX, startRY, width, height, startX, startY, endX, endY, bytearray, spread)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startRX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis of the rectangle which contains the gradient.</td></tr><tr><td style="text-align:left;">startRY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis of the rectangle which contains the gradient.</td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">width of the rectangle.</td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">height of the rectangle.</td></tr><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the start at the x-axis for the gradient.</td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the start at the y-axis for the gradient.</td></tr><tr><td style="text-align:left;">endX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the end at the x-axis for the gradient.</td></tr><tr><td style="text-align:left;">endY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the end at the y-axis for the gradient.</td></tr><tr><td style="text-align:left;">bytearray</td><td style="text-align:left;">ByteArray</td><td style="text-align:left;">bytearray of (position,red,green,blue,alpha,position,red,green,blue,alpha,...). Could be repeated as often you need it, all values have ranges from 0 to 255. The position is a point where the red green blue values are assigned. <br> <strong>Example:</strong> <code>bytearray([0,255,0,0,255,255,0,255,0,127])</code> this is a gradient which starts at 0 with color 255,0,0 and alpha 255 and ends at position 255 with color 0,255,0 and alpha 127. The colors in between are interpolation, so this example is a color shift from red to green.</td></tr><tr><td style="text-align:left;">spread</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Defines the spread method outside the gradient. Available spread modes are: <br> <code>0</code> -&gt; The area is filled with the closest stop color <br> <code>1</code> -&gt; The gradient is reflected outside the gradient area <br> <code>2</code> -&gt; The gradient is repeated outside the gradient area <br> Please note that outside means <em>inside</em> the rectangle but outside of the gradient start and end points, so if these points are the same, you don&#39;t see the spread mode. A picture to the spread modes can you find here: <a href="https://doc.qt.io/qt-5/qlineargradient.html#details" target="_blank" rel="noreferrer">Spread modes</a></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Shorter versions of hyperion.imageLinearGradient()</p><p><code>hyperion.imageLinearGradient(startX, startY, endX, endY, bytearray, spread)</code> -&gt; The rectangle which contains the gradient defaults to the full image</p></div><h3 id="hyperion-imagedrawline" tabindex="-1">hyperion.imageDrawLine() <a class="header-anchor" href="#hyperion-imagedrawline" aria-label="Permalink to &quot;hyperion.imageDrawLine()&quot;">​</a></h3><p>Draws a line at the image. All arguments are required, exception a for alpha. Add the arguments in the order of rows below. <br><code>hyperion.imageDrawLine(startX, startY, endX, endY, thick, r, g, b, a)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">endX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">end point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">endY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">end point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">thick</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Thickness of the line, should be calculated based on image height or width. But at least one Pixel. Example: <code>max(int(0.1*hyperion.imageHeight(),1)</code> is 10% of the image height.</td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Shorter versions of hyperion.imageLinearGradient()</p><p><code>hyperion.imageLinearGradient(startX, startY, endX, endY, bytearray, spread)</code> -&gt; The rectangle which contains the gradient defaults to the full image</p></div><h3 id="hyperion-imagedrawpoint" tabindex="-1">hyperion.imageDrawPoint() <a class="header-anchor" href="#hyperion-imagedrawpoint" aria-label="Permalink to &quot;hyperion.imageDrawPoint()&quot;">​</a></h3><p>Draws a point/dot at the image. All arguments are required, exception a for alpha. Add the arguments in the order of rows below. <br><code>hyperion.imageDrawPoint(x, y, thick, r, g, b, a)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">x</td><td style="text-align:left;">Integer</td><td style="text-align:left;">point position at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">y</td><td style="text-align:left;">Integer</td><td style="text-align:left;">point position at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">thick</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Thickness of the point in pixel, should be calculated based on image height or width. But at least one Pixel. Example: <code>max(int(0.1*hyperion.imageHeight(),1)</code> is 10% of the image height.</td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Shorter versions of hyperion.imageDrawPoint()</p><p><code>hyperion.imageDrawPoint(x, y, thick, r, g, b)</code> -&gt; alpha defaults to 255</p></div><h3 id="hyperion-imagedrawpolygon" tabindex="-1">hyperion.imageDrawPolygon() <a class="header-anchor" href="#hyperion-imagedrawpolygon" aria-label="Permalink to &quot;hyperion.imageDrawPolygon()&quot;">​</a></h3><p>Draws a polygon at the image and fills it with the specific color. Used for free forming (triangle, hexagon,... whatever you want ). All arguments are required, exception a for alpha. Add the arguments in the order of rows below. <br><code>hyperion.imageDrawPolygon(bytearray, r, g, b, a)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">bytearray</td><td style="text-align:left;">ByteArray</td><td style="text-align:left;">bytearray([point1X,point1Y,point2X,point2Y,point3X,point3Y,...]). Add pairs of X/Y coordinates to specific the corners of the polygon, each point has a X and a Y coordinate, you could add as much points as you need. The last point automatically connects to the first point.</td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Shorter versions of hyperion.imageDrawPolygon()</p><p><code>hyperion.imageDrawPolygon(bytearray, r, g, b)</code> -&gt; alpha defaults to 255</p></div><h3 id="hyperion-imagedrawpie" tabindex="-1">hyperion.imageDrawPie() <a class="header-anchor" href="#hyperion-imagedrawpie" aria-label="Permalink to &quot;hyperion.imageDrawPie()&quot;">​</a></h3><p>Draws a pie (also known from pie charts) at the image and fills it with the specific color. All arguments are required, exception a for alpha. Add the arguments in the order of rows below. <br><code>hyperion.imageDrawPie(centerX, centerY, radius, startAngle, spanAngle, r, g, b, a)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">centerX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">The center of the Pie at the x-axis</td></tr><tr><td style="text-align:left;">centerY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">The center of the Pie at the y-axis</td></tr><tr><td style="text-align:left;">radius</td><td style="text-align:left;">Integer</td><td style="text-align:left;">radius of the Pie in Pixels</td></tr><tr><td style="text-align:left;">startAngle</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start angle from <code>0</code> to <code>360</code>. <code>0</code> is at 3 o&#39;clock</td></tr><tr><td style="text-align:left;">spanAngle</td><td style="text-align:left;">Integer</td><td style="text-align:left;">span (wide) of the pie from <code>-360</code> to <code>360</code> which starts at the startAngle, positive values are counter-clockwise, negative clockwise</td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Shorter versions of hyperion.imageDrawPie()</p><p><code>hyperion.imageDrawPie(centerX, centerY, radius, startAngle, spanAngle, r, g, b)</code> -&gt; alpha defaults to 255</p></div><h3 id="hyperion-imagedrawrect" tabindex="-1">hyperion.imageDrawRect() <a class="header-anchor" href="#hyperion-imagedrawrect" aria-label="Permalink to &quot;hyperion.imageDrawRect()&quot;">​</a></h3><p>Draws a rectangle on the image. All arguments are required, exception a for alpha. Add the arguments in the order of rows below. <br><code>hyperion.imageDrawRect(startX, startY, width, height, thick, r, g, b, a,)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">width of the rectangle. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">height of the rectangle. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">thick</td><td style="text-align:left;">Integer</td><td style="text-align:left;">Thickness of the rectangle, a good start value is <code>1</code></td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;"><strong>Optional</strong> alpha of the color from <code>0</code> to <code>255</code>, if not provided, it&#39;s <code>255</code></td></tr></tbody></table><h3 id="hyperion-imagesolidfill" tabindex="-1">hyperion.imageSolidFill() <a class="header-anchor" href="#hyperion-imagesolidfill" aria-label="Permalink to &quot;hyperion.imageSolidFill()&quot;">​</a></h3><p>Fill a specific part of the image with a solid color (or entire). All arguments are required. Add the arguments in the order of rows below. <br><code>hyperion.imageSolidFill(startX, startY, width, height, r, g, b, a)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">startX</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">startY</td><td style="text-align:left;">Integer</td><td style="text-align:left;">start point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">width</td><td style="text-align:left;">Integer</td><td style="text-align:left;">width of the fill area. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">height</td><td style="text-align:left;">Integer</td><td style="text-align:left;">height of the fill area. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define blue color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">a</td><td style="text-align:left;">Integer</td><td style="text-align:left;">alpha of the color from <code>0</code> to <code>255</code></td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Shorter versions of hyperion.imageSolidFill()</p><ul><li><code>hyperion.imageSolidFill(startX, startY, width, height, r, g, b)</code> -&gt; no alpha, defaults to 255</li><li><code>hyperion.imageSolidFill(r, g, b, a)</code> -&gt; startX and startY is 0, width and height is max. -&gt; full image</li><li><code>hyperion.imageSolidFill(r, g, b)</code> -&gt; startX and startY is 0, width and height is max, alpha 255. -&gt; full image</li></ul></div><h3 id="hyperion-imagesetpixel" tabindex="-1">hyperion.imageSetPixel() <a class="header-anchor" href="#hyperion-imagesetpixel" aria-label="Permalink to &quot;hyperion.imageSetPixel()&quot;">​</a></h3><p>Assign a color to a specific pixel position. All arguments are required. Add the arguments in the order of rows below. <br><code>hyperion.imageSetPixel(X, Y, r, g, b)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">X</td><td style="text-align:left;">Integer</td><td style="text-align:left;">pixel point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">Y</td><td style="text-align:left;">Integer</td><td style="text-align:left;">pixel point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">r</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define red color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">g</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define green color from <code>0</code> to <code>255</code></td></tr><tr><td style="text-align:left;">b</td><td style="text-align:left;">Integer</td><td style="text-align:left;">define blue color from <code>0</code> to <code>255</code></td></tr></tbody></table><h3 id="hyperion-imagegetpixel" tabindex="-1">hyperion.imageGetPixel() <a class="header-anchor" href="#hyperion-imagegetpixel" aria-label="Permalink to &quot;hyperion.imageGetPixel()&quot;">​</a></h3><p>Get a color of a specific pixel position. All arguments are required. Add the arguments in the order of rows below. <br><code>hyperion.imageGetPixel(X, Y)</code></p><table><thead><tr><th style="text-align:left;">Argument</th><th style="text-align:left;">Type</th><th style="text-align:left;">Comment</th></tr></thead><tbody><tr><td style="text-align:left;">X</td><td style="text-align:left;">Integer</td><td style="text-align:left;">pixel point at the x-axis. Relates to <code>hyperion.imageWidth()</code></td></tr><tr><td style="text-align:left;">Y</td><td style="text-align:left;">Integer</td><td style="text-align:left;">pixel point at the y-axis. Relates to <code>hyperion.imageHeight()</code></td></tr><tr><td style="text-align:left;">Return</td><td style="text-align:left;">Tuple</td><td style="text-align:left;">Returns a Python Tuple of RGB values</td></tr></tbody></table><h3 id="hyperion-setcolor" tabindex="-1">hyperion.setColor() <a class="header-anchor" href="#hyperion-setcolor" aria-label="Permalink to &quot;hyperion.setColor()&quot;">​</a></h3><p>Set a single color to all LEDs by adding <code>hyperion.setColor(255,0,0)</code>, all LEDs will be red. But it is also possible to send a bytearray of RGB values. Each RGB value in this bytearray represents one LED.</p><ul><li><strong>Example 1:</strong> <code>hyperion.setColor(bytearray([255,0,0]))</code> The first LED will be red</li><li><strong>Example 2:</strong> <code>hyperion.setColor(bytearray([255,0,0,0,255,0]))</code> The first LED will be red, the second is green</li><li><strong>Example 3:</strong> <code>hyperion.setColor(bytearray([255,0,0,0,255,0,255,255,255]))</code> The first LED will be red, the second is green, the third is white</li><li>You usually assign to all LEDs a color, therefore you need to know how much LEDs the user currently have. Get it with <code>hyperion.ledCount</code></li></ul><div class="warning custom-block"><p class="custom-block-title">hyperion.setColor()</p><ul><li>hyperion.setColor() function is not recommended to assign LED colors, it doesn&#39;t work together with <strong><code>hyperion.image*</code></strong> functions</li><li>You don&#39;t know where is top/left/right/bottom and it doesn&#39;t work with matrix layouts!</li><li>Please consider to use the <strong><code>hyperion.image*</code></strong> functions instead to create amazing effects that scales with the user setup</li></ul></div>',67),d=[r];function o(n,s,h,g,y,c){return a(),e("div",null,d)}const x=t(i,[["render",o]]);export{p as __pageData,x as default};
